mapper.select:

select class_id, classname, school_ID, schoolname, grade, teachername, floor, create_time
from tl_school_class
where class_id = #{classId,jdbcType=INTEGER}

mapper.insert:

insert into tl_school_educational_administration(
schoolname, classname,start_time, cource, teachername, status
)
values (
#{schoolname}, #{classname}
#{start_time}, #{cource}
#{teachername}, #{status}
)
